	public double getH (double x, double y, double z){
		double p = map[(int)x][(int)y].z;
		if(Math.abs(x%1) + 1- Math.abs(y%1) <= 1){
			p+= (map[(int) (x)][(int) (y) + 1].z-map[(int) (x)][(int) y].z) * (y%1) +(-map[(int) (x)][(int) (y +1)].z+map[(int) (x+1)][(int) (y) +1].z) *(x%1) ;
		}else{
			p-= (map[(int) x][(int) y].z-map[(int) (x+1)][(int) y].z) * (x%1) +(map[(int) (x+1)][(int) (y)].z-map[(int) (x+1)][(int) (y) +1].z) *(y%1) ;
		}
		return p +.8;
	}
	public String toString (){
		String total= "{{";
		for(int x =0; x< map.length; x++){
			for(int y =0; y<map[x].length; y++){
				total += map[x][y].z + ", ";
			}
			total = total.substring(0, total.length()-2);
			total+="},\n{";
		}
		total = total.substring(0, total.length()-3);
		total+="}";
		
		return total;
	}
