	public void draw(Vector dir, Point pos, Graphics g, double k, double l, double disx, double disy, double cosx, double sinx, double cosy, double siny){
		Vector[][] v = new Vector[map.length][map[0].length];
		for(int x=0; x<v.length; x++){
			for(int y = 0; y < v[x].length; y++)
				v[x][y]=dir.rotateView(pos, map[x][y]);
		}
		for(int x=0; x< pos.x ; x++){
			for(int y = 0; y < pos.y; y++){
				this.drawblock(x, y, v, dir, pos, g, k, l, disx, disy);
			}
		}
		for(int x=0; x< pos.x ; x++){
			for(int y = v[x].length -2; y > pos.y; y--){
				this.drawblock(x, y, v, dir, pos, g, k, l, disx, disy);
			}
		}
		for(int x=v.length-2; x> pos.x ; x--){
			for(int y = v[x].length -2; y > pos.y; y--){
				this.drawblock(x, y, v, dir, pos, g, k, l, disx, disy);
			}
		}for(int x=v.length-2; x> pos.x ; x--){
			for(int y = 0; y < pos.y; y++){
				this.drawblock(x, y, v, dir, pos, g, k, l, disx, disy);
			}
		}
		
	}
	public void drawblock(int x, int y, Vector[][] v, Vector dir, Point pos, Graphics g, double k, double l, double disx, double disy){
		
		g.setColor(Color.black);
		Polygon b = new Polygon();
		b.addPoint((float) ((v[x][y].dx + k)*disx), (float) ((v[x][y].dy + l)*disy));
		b.addPoint((float) ((v[x+1][y].dx + k)*disx), (float) ((v[x+1][y].dy + l)*disy));
		b.addPoint((float) ((v[x+1][y+1].dx + k)*disx), (float) ((v[x+1][y+1].dy + l)*disy));
		//g.fill(b);
		b = new Polygon();
		b.addPoint((float) ((v[x][y].dx + k)*disx), (float) ((v[x][y].dy + l)*disy));
		b.addPoint((float) ((v[x][y+1].dx + k)*disx), (float) ((v[x][y+1].dy + l)*disy));
		b.addPoint((float) ((v[x+1][y+1].dx + k)*disx), (float) ((v[x+1][y+1].dy + l)*disy));
		//g.fill(b);
		g.setColor(Color.blue);
		if(v[x][y].dx < 5 &&  v[x][y].dx > -5  && v[x][y+1].dy < 5 && v[x][y+1].dy>-5){
			//g.setColor(Color.blue);
			g.drawLine((float)(( v[x][y].dx + k)*disx), (float)(( v[x][y].dy + l)* disy), (float)(( v[x][y+1].dx + k)* disx),(float)(( v[x][y+1].dy + l)*disy));
		}
		if(v[x][y].dx < 5 &&  v[x][y].dx > -5  && v[x+1][y].dy < 5 && v[x+1][y].dy>-5)
			//g.setColor(Color.red);
			g.drawLine((float)(( v[x][y].dx + k)*disx), (float)(( v[x][y].dy + l)* disy), (float)(( v[x+1][y].dx + k)* disx),(float)(( v[x+1][y].dy + l)*disy));
		if(v[x][y].dx < 5 &&  v[x][y].dx > -5  && v[x+1][y + 1].dy < 5 && v[x+1][y + 1].dy>-5)
			//g.setColor(Color.green);
			g.drawLine((float)(( v[x][y].dx + k)*disx), (float)(( v[x][y].dy + l)* disy), (float)(( v[x+1][y + 1].dx + k)* disx),(float)(( v[x+1][y + 1].dy + l)*disy));
	}
	public boolean testSlope(int x, int y, double slope){
		
		
		return Math.abs(map[x][y].z - map[x+1][y].z) > slope || Math.abs(map[x][y].z - map[x][y+1].z )> slope;
	}
	public Point getGradient(int x, int y){
		return new Point ((map[x][y].z - map[x+1][y].z), (map[x][y].z - map[x][y+1].z),  0);
	}
	
	
